package pop.webcobranzas.procesos;

import com.itextpdf.text.Document;
import com.itextpdf.text.Image;
import com.itextpdf.text.pdf.PdfWriter;
import java.io.FileOutputStream;
import org.apache.commons.codec.binary.Base64;

/**
 *
 * @author Jyoverar
 */
public class demoImg {

    /**
     * @param args the command line arguments
     */
    public static void main(String[] args) {
        // TODO code application logic here

        Document document = new Document();
        //
        String b64Image = ""; // .gif and .jpg are ok too!
        String output = "c:/adb/output.pdf";
        try {
            PdfWriter.getInstance(document, new FileOutputStream(output));
            document.open();
            byte[] decoded = Base64.decodeBase64(b64Image.getBytes());
            document.add(Image.getInstance(decoded));
            document.close();
        } catch (Exception e) {
            e.printStackTrace();
        }

    }

}
